## Create song list in DB on first page load#from django.core.signals import request_starteddefault_app_config = 'songs.apps.SongsConfig'# Create song list def load_callback(sender, **kwargs):#    print('Start-up time 2!')    from . import CSD                        # load data in DB by creating SongList     CSD.SongList('list_stp.txt', 'Results_20_02_10_21_00.csv')                        # disable call-back    request_started.disconnect(load_callback)                        # register call-backrequest_started.connect(load_callback)